// Generated by Selenium IDE
const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('Admin Approve/View Approved Events', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('chrome').build()
    vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })
  it('Admin Approve/View Approved Events', async function() {
    // Test name: Admin Approve/View Approved Events
    // Step # | name | target | value
    // 1 | open | http://localhost:3000/ | 
    await driver.get("http://localhost:3000/")
    // 2 | click | id=email | 
    await driver.findElement(By.id("email")).click()
    // 3 | type | id=email | hanyelbob99@gmail.com
    await driver.findElement(By.id("email")).sendKeys("hanyelbob99@gmail.com")
    // 4 | click | id=password | 
    await driver.findElement(By.id("password")).click()
    // 5 | type | id=password | capstone123
    await driver.findElement(By.id("password")).sendKeys("capstone123")
    // 6 | click | id=btn | 
    await driver.findElement(By.id("btn")).click()
    // 7 | click | id=eventImage | 
    await driver.findElement(By.id("eventImage")).click()
    // 8 | assertElementPresent | css=.main-detail-left > .text:nth-child(1) > .text-content | 
    {
      const elements = await driver.findElements(By.css(".main-detail-left > .text:nth-child(1) > .text-content"))
      assert(elements.length)
    }
    // 9 | click | css=.status-btn:nth-child(2) | 
    await driver.findElement(By.css(".status-btn:nth-child(2)")).click()
    // 10 | click | id=eventImage | 
    await driver.findElement(By.id("eventImage")).click()
    // 11 | click | css=.logout-btn:nth-child(1) | 
    await driver.findElement(By.css(".logout-btn:nth-child(1)")).click()
    // 12 | click | css=.status-btn:nth-child(1) | 
    await driver.findElement(By.css(".status-btn:nth-child(1)")).click()
    // 13 | open | http://localhost:3000/dashboard | 
    await driver.get("http://localhost:3000/dashboard")
    // 14 | click | css=.event-banner:nth-child(1) | 
    await driver.findElement(By.css(".event-banner:nth-child(1)")).click()
    // 15 | click | css=.event-banner:nth-child(1) .time-place | 
    await driver.findElement(By.css(".event-banner:nth-child(1) .time-place")).click()
    // 16 | assertElementPresent | css=.itinerary-description | 
    {
      const elements = await driver.findElements(By.css(".itinerary-description"))
      assert(elements.length)
    }
  })
})
